command:example.dev:code:
@'
configuration stuff
{
    Import-DscResource -ModuleName nx

    node localhost
    {
        nxFile f1
        {
            DestinationPath = '/var/opt/somefile.ini'
        }
    }
}
'@ | Set-Content .\ConfigurationFile.ps1
.\ConfigurationFile.ps1

Get-DscConfigurationImportedResource -Name stuff

